import React, { useContext, useEffect, Link } from 'react';
import SearchResults from '../books/SearchResults';
import BookSearch from '../books/BookSearch';
import Collection from '../books/Collection';
import AuthContext from '../../context/auth/authContext';

const Home = () => {
  const authContext = useContext(AuthContext);
  const { isAuthenticated } = authContext;
  useEffect(() => {
    authContext.loadUser();
    // eslint-disable-next-line
  }, []);

  if (isAuthenticated) {
    return (
      <div className="mx-auto">
        <BookSearch />
        <SearchResults />
        <Collection />
      </div>
    );
  } else {
    return (
      <div className="mx-auto">
        <BookSearch />
        <SearchResults />

        <h1 className="text-xl text-center">Please to add books to your collection.</h1>
      </div>
    );
  }
};

export default Home;
