import React, { useReducer } from 'react';
import axios from 'axios';
import BookContext from './bookContext';
import BookReducer from './bookReducer';
import xml2js from 'xml2js';
import { SEARCH_BOOKS } from '../types';

const BookState = (props) => {
  const initialState = {
    books: [],
    book: {},
    loading: false,
  };

  const key = 'e3tRV8bBX3V9UzUCLr0A';

  const [state, dispatch] = useReducer(BookReducer, initialState);

  // Search Books
  const searchBooks = async (text) => {
    const xml = await axios.get(`https://cors-anywhere.herokuapp.com/https://www.goodreads.com/search/index.xml?key=${key}&q=${text}`);
    const res = xml2js.parseString(xml, (err, res) => {
      console.log(res);
      console.log(err);
    });
    dispatch({ type: SEARCH_BOOKS, payload: res });
  };

  return (
    <BookContext.Provider
      value={{
        books: state.books,
        // book: state.book,
        // loading: state.loading,
        searchBooks,
      }}
    >
      {props.children}
    </BookContext.Provider>
  );
};

export default BookState;
