import React from 'react';
import { title, author, desc } from '../../dummies/books.json';

const BookCard = (title, author, desc) => {
  return (
    <div className="max-w-sm rounded overflow-hidden shadow-lg mx-auto my-4">
      {/* <img className="w-full" src={require('../../assets/img/profile.jpg')} alt="Display" /> */}
      <div className="px-6 py-4">
        <div className="font-bold text-red-500 text-xl mb-2">{title}</div>
        <p className="text-gray-700 text-base">{desc}</p>
      </div>
      <div className="px-6 py-4">
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">{author}</span>
      </div>
    </div>
  );
};

export default BookCard;
